import './Women.css'
import { ApolloClient, 
          InMemoryCache, 
          ApolloProvider, 
          HttpLink, 
          from 
} from '@apollo/client';
import { onError } from '@apollo/client/link/error'
import { useQuery, gql } from "@apollo/client";

const errorLink = onError(({ graphqlErrors, networkError }) => {
  if (graphqlErrors) {
    graphqlErrors.map(({ message, location, path }) => {
      alert(`Graphql `)
    })
  }
})

const link =  from([
  errorLink,
  new HttpLink({ uri: "https://github.com/scandiweb/junior-react-endpoint/tree/master/src/data"})
]);

const client = new ApolloClient({
  cache: new InMemoryCache(),
  link: link
})

const query = gql`
  {
    Products(limit: 10) {
      id
      category
    }
  }
`;

function Women() {
  const { product, loading, error } = useQuery(query);

  if (loading) return (
      <p> Loading </p>
      );
  if (error) return (
    <pre>{error.message}</pre>
  )

  return (
    <div className="Women">
      <h1>Category Name</h1>
        <div className='boxes'>
              <div className="box">
                {product.Products.map((item) => (
                  <div key={item.id}>{item.category}</div>
                ))}
              </div>
              <div className="box">
                {product.Products.map((item) => (
                  <div key={item.id}>{item.category}</div>
                ))}
              </div>
              <div className="box">
                {product.Products.map((item) => (
                  <div key={item.id}>{item.category}</div>
                ))}
              </div>
                <div className="box">
                0
                </div>
                <div className="box">
                0
                </div>
                <div className="box">
                0
                </div>
        </div>
  
    </div>
  )
}

export default Women
