import "./Display.css"
import { useState, useEffect } from "react";


function ProductsList({children}) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    items()
  }, [])

  // Fetched items
  const items = async () => {
    const res = await fetch(`https://fakestoreapi.com/products`)
    const data = await res.json()

    setProducts(data)
  } 

  return (
    <div>
      <div>
        <p>
          {products.map(products => products.id[1]) }
          {products.map(products => products.title) }
          {products.map(products => products.description[1]) }
        <img src={`${products.map(products => products.image) }`} alt="" className="img" />
        </p>
        {children}
      </div>
      <div className='box'>
        <p> {products.id} 
          {products.title}
          {products.description}
          <img src={`${products.image}`} alt="" className="img" />
        </p>
        {children}
      </div>
    </div>
  )
}

export default ProductsList
