import "./Display.css"
import { useState, useEffect } from "react";


function ProductsList({children}) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    items()
  }, [])

  // Fetched items
  const items = async () => {
    const res = await fetch(`https://fakestoreapi.com/products/categories`)
    const data = await res.json()

    setProducts(data)
  } 

  return (
    <>
    <div className="box">
        <p>
          {products.map(products => products.id) }
          {products.map(products => products.title) }
          {products.map(products => products.description) }
        <img src={`${products.map(products => products.image) }`} alt="" className="img" />
        </p>
        {children}
      </div>
      <div className="box">
        <p>
          {products.map(products => products.id) }
          {products.map(products => products.title) }
          {products.map(products => products.description) }
          <img src='https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg' alt="" />
        </p>

        <div>
        <img src='https://fakestoreapi.com/img/71-3HjGNDUL._AC_SY879._SX._UX._SY._UY_.jpg' alt="" />
        <img src='https://fakestoreapi.com/img/71li-ujtlUL._AC_UX679_.jpg' alt="" />
        <img src='https://fakestoreapi.com/img/71YXzeOuslL._AC_UY879_.jpg' alt="" />
 
        <img src='https://fakestoreapi.com/img/71pWzhdJNwL._AC_UL640_QL65_ML3_.jpg' alt="" />
        <img src='https://fakestoreapi.com/img/61sbMiUnoGL._AC_UL640_QL65_ML3_.jpg' alt="" />
        <img src='https://fakestoreapi.com/img/71YAIFU48IL._AC_UL640_QL65_ML3_.jpg' alt="" />
 
        <img src='https://fakestoreapi.com/img/51UDEzMJVpL._AC_UL640_QL65_ML3_.jpg' alt="" />
        <img src='https://fakestoreapi.com/img/61IBBVJvSDL._AC_SY879_.jpg' alt="" />
   
        </div>
        {children}
      </div>
  </>
  )
}

export default ProductsList
