import "./Display.css"
import { useState, useEffect } from "react";


function ProductsList({children}) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    items()
  }, [])

  // Fetched items
  const items = async () => {
    const res = await fetch(`https://fakestoreapi.com/products/2`)
    const data = await res.json()

    setProducts(data)
  } 

return (
  <div className="boxes">
    <div className="box">
      <div key={products.id} className="box">
        {products.title }
        {products.description }
        <img src={`${products.image }`} alt="" className="img" />
    </div>
    {children}
    </div>
  </div>
  )
}

export default ProductsList
