import { useQuery, gql } from '@apollo/client';

const PRODUCTS = gql`
query {
    products(last: 10) {
      edges {
        cursor
        node {
          id
          name
        }
      }
  }
`;

function Display() {
  const { loading, error, product } = useQuery(PRODUCTS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error : {error.message}</p>;

  return product.edges.map?.map(({ id, name, description }) => (
    <div className='box'>
        <div key={id}>
        <h3>{name}</h3>
        <br />
        <b>About this location:</b>
        <p>{description}</p>
        <br />
        </div>
    </div>
  ));
}

export default Display
